import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { io } from "socket.io-client";

const socket = io("http://localhost:4000");

export default function GamePage() {
  const { gameId } = useParams();
  const [username, setUsername] = useState(localStorage.getItem("username") || "");
  const [players, setPlayers] = useState([]);
  const [vote, setVote] = useState(null);
  const [showVotes, setShowVotes] = useState(false);

  const fibonacciNumbers = [0, 1, 2, 3, 5, 8, 13, 21, 34, 65];

  useEffect(() => {
    if (username) {
      socket.emit("joinGame", { gameId, username });
    }
  }, [username, gameId]);

  useEffect(() => {
    socket.on("updatePlayers", (players) => {
      setPlayers(players);
    });

    socket.on("revealVotes", (players) => {
      setPlayers(players);
      setShowVotes(true);
    });

    socket.on("gameRestarted", () => {
      setVote(null);
      setShowVotes(false);
    });

    return () => {
      socket.off("updatePlayers");
      socket.off("revealVotes");
      socket.off("gameRestarted");
    };
  }, []);

  const handleVote = (num) => {
    setVote(num);
    socket.emit("vote", { gameId, username, vote: num });
  };

  const handleReveal = () => {
    socket.emit("reveal", { gameId });
  };

  const handleRestart = () => {
    socket.emit("restartGame", { gameId });
  };

  if (!username) {
    return (
      <div className="flex flex-col items-center justify-center h-[80vh]">
        <h2 className="text-2xl mb-4">Enter your username to join</h2>
        <input
          type="text"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          className="border px-4 py-2 mb-4"
        />
        <button
          onClick={() => {
            localStorage.setItem("username", username);
            socket.emit("joinGame", { gameId, username });
          }}
          className="bg-blue-500 text-white px-6 py-2 rounded"
        >
          Join Game
        </button>
      </div>
    );
  }

  return (
    <div className="p-6">
      <h2 className="text-xl font-bold mb-4">Game ID: {gameId}</h2>

      <div className="mb-4">
        <h3 className="font-bold mb-2">Players:</h3>
        <ul>
          {players.map((player, i) => (
            <li key={i}>
              {player.username} —{" "}
              {showVotes ? player.vote : player.vote ? "✅" : "❌"}
            </li>
          ))}
        </ul>
      </div>

      <div className="flex gap-2 mb-4">
        {fibonacciNumbers.map((num) => (
          <button
            key={num}
            onClick={() => handleVote(num)}
            disabled={vote !== null}
            className={`px-4 py-2 border rounded ${
              vote === num ? "bg-green-300" : "bg-gray-200"
            }`}
          >
            {num}
          </button>
        ))}
      </div>

      <div className="flex gap-4">
        <button
          onClick={handleReveal}
          className="bg-yellow-500 text-white px-4 py-2 rounded"
        >
          Reveal Votes
        </button>
        <button
          onClick={handleRestart}
          className="bg-red-500 text-white px-4 py-2 rounded"
        >
          Restart Game
        </button>
      </div>
    </div>
  );
}
