import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { v4 as uuidv4 } from 'uuid';

export default function LandingPage() {
  const navigate = useNavigate();
  const [username, setUsername] = useState('');

  const handleStartGame = () => {
    if (!username.trim()) {
      alert('Please enter a username');
      return;
    }

    // Save username for later use
    localStorage.setItem('username', username.trim());

    // Create a new gameId
    const gameId = uuidv4();

    // Navigate to game page
    navigate(`/game/${gameId}`);
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100 dark:bg-gray-900 transition-colors">
      {/* Header */}
      <div className="absolute top-4 left-4 flex items-center gap-2">
        <img src="/logo.png" alt="Logo" className="w-10 h-10" />
        <h1 className="text-xl font-bold text-gray-900 dark:text-white">Planning Poker</h1>
      </div>
      <div className="absolute top-4 right-4">
        {/* This is where your dark/light mode toggle button will be rendered from Layout */}
      </div>

      {/* Main content */}
      <h2 className="text-3xl font-bold text-gray-800 dark:text-white mb-6">
        Set your team points
      </h2>

      <input
        type="text"
        placeholder="Enter username"
        value={username}
        onChange={(e) => setUsername(e.target.value)}
        className="px-4 py-2 border rounded mb-4 w-64 text-center bg-white dark:bg-gray-800 dark:text-white"
      />

      <button
        onClick={handleStartGame}
        className="bg-blue-500 text-white px-6 py-2 rounded hover:bg-blue-600"
      >
        Start Game
      </button>
    </div>
  );
}
