import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import io from 'socket.io-client';

const socket = io('http://localhost:4000'); // backend server

export default function GamePage() {
  const { gameId } = useParams();
  const [players, setPlayers] = useState([]);
  const [vote, setVote] = useState(null);
  const [revealed, setRevealed] = useState(false);

  const numbers = [0, 1, 2, 3, 5, 8, 13, 21, 34, 65];

  useEffect(() => {
    const username = localStorage.getItem('username');
    if (!username) {
      alert('No username found! Returning to home.');
      window.location.href = '/';
      return;
    }

    socket.emit('joinGame', { gameId, username });

    socket.on('updatePlayers', (players) => {
      setPlayers(players);
      setRevealed(false);
    });

    socket.on('revealVotes', (players) => {
      setPlayers(players);
      setRevealed(true);
    });

    socket.on('gameRestarted', () => {
      setVote(null);
      setRevealed(false);
    });

    return () => {
      socket.off('updatePlayers');
      socket.off('revealVotes');
      socket.off('gameRestarted');
    };
  }, [gameId]);

  const handleVote = (num) => {
    const username = localStorage.getItem('username');
    setVote(num);
    socket.emit('vote', { gameId, username, vote: num });
  };

  const handleReveal = () => {
    socket.emit('reveal', { gameId });
  };

  const handleRestart = () => {
    socket.emit('restartGame', { gameId });
  };

  return (
    <div>
      {/* Player Cards */}
      <div className="grid grid-cols-2 gap-4 mb-6">
        {players.map((player) => (
          <div
            key={player.id}
            className="p-4 border rounded bg-white dark:bg-gray-800 text-center"
          >
            <p className="font-bold">{player.username}</p>
            <p className="text-lg">
              {revealed
                ? player.vote ?? '—'
                : player.vote !== null
                ? '✅'
                : '❓'}
            </p>
          </div>
        ))}
      </div>

      {/* Voting Numbers */}
      <div className="flex gap-2 mb-4 flex-wrap">
        {numbers.map((num) => (
          <button
            key={num}
            onClick={() => handleVote(num)}
            disabled={vote !== null}
            className={`px-4 py-2 rounded border ${
              vote === num ? 'bg-blue-500 text-white' : 'bg-gray-200 dark:bg-gray-700'
            }`}
          >
            {num}
          </button>
        ))}
      </div>

      {/* Actions */}
      <div className="flex gap-4">
        <button
          onClick={handleReveal}
          className="px-4 py-2 bg-green-500 text-white rounded"
        >
          Reveal Votes
        </button>
        <button
          onClick={handleRestart}
          className="px-4 py-2 bg-yellow-500 text-white rounded"
        >
          Restart Game
        </button>
      </div>
    </div>
  );
}
