// frontend/src/pages/GamePage.js
import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import io from "socket.io-client";

const socket = io("http://localhost:4000");

export default function GamePage() {
  const { gameId } = useParams();
  const [username, setUsername] = useState(localStorage.getItem("username") || "");
  const [players, setPlayers] = useState([]);
  const [vote, setVote] = useState(null);
  const [votesRevealed, setVotesRevealed] = useState(false);

  useEffect(() => {
    if (username) {
      socket.emit("joinGame", { gameId, username });
    }

    socket.on("updatePlayers", (players) => {
      setPlayers(players);
    });

    socket.on("revealVotes", (players) => {
      setVotesRevealed(true);
      setPlayers(players);
    });

    socket.on("gameRestarted", () => {
      setVotesRevealed(false);
      setVote(null);
    });

    return () => {
      socket.off("updatePlayers");
      socket.off("revealVotes");
      socket.off("gameRestarted");
    };
  }, [username, gameId]);

  const handleJoin = () => {
    if (!username.trim()) {
      alert("Enter your name");
      return;
    }
    localStorage.setItem("username", username);
    socket.emit("joinGame", { gameId, username });
  };

  const handleVote = (num) => {
    setVote(num);
    socket.emit("vote", { gameId, username, vote: num });
  };

  const handleReveal = () => {
    socket.emit("reveal", { gameId });
  };

  const handleRestart = () => {
    socket.emit("restartGame", { gameId });
  };

  const handleCopyLink = () => {
    navigator.clipboard.writeText(window.location.href);
    alert("Link copied!");
  };

  if (!username) {
    // Prompt for username if missing
    return (
      <div className="flex flex-col items-center justify-center h-screen">
        <h2 className="text-2xl font-bold mb-4">Enter your username to join the game</h2>
        <input
          className="border px-4 py-2 mb-4"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          placeholder="Your name"
        />
        <button
          className="bg-blue-500 text-white px-4 py-2 rounded"
          onClick={handleJoin}
        >
          Join Game
        </button>
      </div>
    );
  }

  return (
    <div className="p-4">
      {/* Header */}
      <div className="flex justify-between mb-6">
        <div className="flex items-center gap-4">
          <h1 className="text-xl font-bold">Planning Poker</h1>
          <button onClick={handleCopyLink} className="bg-gray-300 px-2 py-1 rounded">
            Copy Link
          </button>
        </div>
        <button
          onClick={handleRestart}
          className="bg-red-500 text-white px-4 py-2 rounded"
        >
          Restart Game
        </button>
      </div>

      {/* Player List */}
      <div className="mb-6">
        <h2 className="text-lg font-semibold mb-2">Players</h2>
        <ul>
          {players.map((p) => (
            <li key={p.id}>
              {p.username}{" "}
              {votesRevealed ? `(Vote: ${p.vote})` : p.vote !== null ? "(Voted)" : "(Not Voted)"}
            </li>
          ))}
        </ul>
      </div>

      {/* Voting Buttons */}
      <div className="flex gap-2 mb-4">
        {[0, 1, 2, 3, 5, 8, 13, 21, 34, 65].map((num) => (
          <button
            key={num}
            className={`px-4 py-2 rounded border ${
              vote === num ? "bg-blue-500 text-white" : "bg-white"
            }`}
            onClick={() => handleVote(num)}
            disabled={votesRevealed}
          >
            {num}
          </button>
        ))}
      </div>

      {/* Reveal Button */}
      {!votesRevealed && (
        <button
          onClick={handleReveal}
          className="bg-green-500 text-white px-4 py-2 rounded"
        >
          Reveal Votes
        </button>
      )}
    </div>
  );
}
