import React, { useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import socket from '../socket.js';

const JoinGame = () => {
  const [username, setUsername] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const { gameId } = useParams();
  const navigate = useNavigate();

  const handleJoinGame = () => {
    if (!username.trim()) {
      setError('Please enter your username');
      return;
    }

    setLoading(true);
    setError('');

    // Store username in localStorage
    localStorage.setItem('username', username.trim());

    // Emit join game event
    socket.emit('joinGame', {
      gameId: gameId,
      username: username.trim()
    });

    // Listen for join response
    socket.once('joinGameResponse', (response) => {
      setLoading(false);
      if (response.success) {
        // Navigate to the game page with the gameId
        navigate(`/game/${gameId}`);
      } else {
        setError(response.message || 'Failed to join the game');
      }
    });
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleJoinGame();
    }
  };

  return (
    <div style={{
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      padding: '20px',
      fontFamily: 'Arial, sans-serif'
    }}>
      <div style={{
        backgroundColor: 'white',
        borderRadius: '12px',
        boxShadow: '0 10px 25px rgba(0, 0, 0, 0.2)',
        padding: '40px',
        maxWidth: '400px',
        width: '100%',
        textAlign: 'center'
      }}>
        <div style={{ marginBottom: '30px' }}>
          <img 
            src={require('../images/koneLogo.png')} 
            alt="KONE Logo" 
            style={{
              height: '60px',
              marginBottom: '20px'
            }}
          />
        </div>
        
        <h1 style={{
          color: '#333',
          fontSize: '28px',
          fontWeight: '600',
          marginBottom: '10px',
          margin: '0 0 10px 0'
        }}>
          Join Planning Poker
        </h1>
        
        <p style={{
          color: '#666',
          fontSize: '16px',
          marginBottom: '30px',
          margin: '0 0 30px 0'
        }}>
          Enter your username to join the game
        </p>
        
        <div style={{ marginBottom: '20px' }}>
          <label 
            htmlFor="username"
            style={{
              display: 'block',
              color: '#555',
              fontSize: '14px',
              fontWeight: '500',
              marginBottom: '8px',
              textAlign: 'left'
            }}
          >
            Your Username
          </label>
          <input
            type="text"
            id="username"
            placeholder="Enter your username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            onKeyPress={handleKeyPress}
            disabled={loading}
            maxLength={20}
            style={{
              width: '100%',
              padding: '12px 16px',
              border: '2px solid #e1e5e9',
              borderRadius: '8px',
              fontSize: '16px',
              outline: 'none',
              transition: 'border-color 0.3s ease',
              boxSizing: 'border-box',
              backgroundColor: loading ? '#f5f5f5' : 'white',
              cursor: loading ? 'not-allowed' : 'text'
            }}
            onFocus={(e) => e.target.style.borderColor = '#667eea'}
            onBlur={(e) => e.target.style.borderColor = '#e1e5e9'}
            autoFocus
          />
        </div>
        
        {error && (
          <div style={{
            backgroundColor: '#fee',
            color: '#c53030',
            padding: '10px',
            borderRadius: '6px',
            fontSize: '14px',
            marginBottom: '20px',
            border: '1px solid #fed7d7'
          }}>
            {error}
          </div>
        )}
        
        <button 
          onClick={handleJoinGame}
          disabled={loading || !username.trim()}
          style={{
            width: '100%',
            padding: '14px',
            fontSize: '16px',
            fontWeight: '600',
            color: 'white',
            backgroundColor: (loading || !username.trim()) ? '#a0a0a0' : '#667eea',
            border: 'none',
            borderRadius: '8px',
            cursor: (loading || !username.trim()) ? 'not-allowed' : 'pointer',
            transition: 'all 0.3s ease',
            marginBottom: '20px'
          }}
          onMouseEnter={(e) => {
            if (!loading && username.trim()) {
              e.target.style.backgroundColor = '#5a6fd8';
              e.target.style.transform = 'translateY(-1px)';
            }
          }}
          onMouseLeave={(e) => {
            if (!loading && username.trim()) {
              e.target.style.backgroundColor = '#667eea';
              e.target.style.transform = 'translateY(0)';
            }
          }}
        >
          {loading ? 'Joining...' : 'Join Game'}
        </button>
        
        <div style={{
          backgroundColor: '#f8f9fa',
          padding: '15px',
          borderRadius: '8px',
          border: '1px solid #e9ecef'
        }}>
          <p style={{
            color: '#6c757d',
            fontSize: '14px',
            margin: '0'
          }}>
            Game ID: <strong style={{ color: '#495057' }}>{gameId}</strong>
          </p>
        </div>
      </div>
    </div>
  );
};

export default JoinGame;
