import React, { useState,  } from 'react';
import { useNavigate } from 'react-router-dom';
import { v4 as uuidv4 } from 'uuid';

export default function LandingPage() {
  const [username, setUsername] = useState('');
  const [fade,setFade] = useState(true);
  const [index,setIndex] = useState(0);
  const navigate = useNavigate();

  const message = [
    "We plan together, we win together...",
    "Every point counts when the team counts...",
    "Collaboration is the real currency...",
    "Estimate smart, deliver smarter...",
    "Turn uncertainty into clarity..."
  ]

  useEffect(() => {
    const interval = setInterval(() => {
      setFade(false); 
      setTimeout(() => {
        setIndex((prevIndex) => (prevIndex + 1) % message.length);
        setFade(true);
      }, 500);
    }, 5000);

    return () => clearInterval(interval);
  }, [message.length]);

  const handleStart = () => {
    if (!username.trim()) {
      alert('Please enter a username');
      return;
    }

    // Save username to localStorage
    localStorage.setItem('username', username);

    // Create a new game ID
    const gameId = uuidv4();

    // Redirect to game page
    navigate(`/game/${gameId}`);
  };

  return (
    <div className="flex flex-col items-center justify-center h-[80vh]">
      <h1
        className="modeChange"
        style={{
          position: "absolute",
          top: "25%",
          left: "50%",
          transform: "translate(-50%, -50%)",
          color: "#004798ff",
          fontSize: "36px",
          textAlign: "center",
        }}
      >
        Welcome to Planning Poker!!
      </h1>

      <h2 className={`slide-text ${fade ? "slide-in" : "slide-out"}`}
        style={{
          position: "absolute",
          top: "40%",
          left: "50%",
          transform: "translate(-50%, -50%)",
          color: "#51b1ffff",
          fontSize: "26px",
          fontStyle: "italic",
        }}
      >
        {message[index]}
      </h2>

      <div
        style={{
          position: "absolute",
          top: "60%",
          left: "50%",
          transform: "translate(-50%, -50%)",
          textAlign: "center",
        }}
      ></div>
      <input
        type="text"
        placeholder="Enter your username"
        value={username}
        onChange={(e) => setUsername(e.target.value)}
        style={{
            width: "300px",
            padding: "12px",
            border: "2px solid #0068dfff",
            borderRadius: "8px",
            fontSize: "16px",
            outline: "none",
            transition: "border-color 0.3s",
            margin: "0 auto",
          }}
      />
      <button
        onClick={handleStart}
        style={{
          width: "110px",
          height: "40px",
          margin: "10px",
          fontSize: "14px",
          color: "white",
          backgroundColor: "#0068dfff",
          border: "none",
          borderRadius: "5px",
        }}
      >
        Start Game
      </button>
    </div>
  );
}
