import React, { useEffect, useState } from 'react';
import { useLocation, useParams } from 'react-router-dom';
import socket from '../socket';

export default function GamePage() {
  const { gameId } = useParams();
  const { state } = useLocation();
  const username = state?.username || 'Guest';

  const [players, setPlayers] = useState([]);
  const [revealed, setRevealed] = useState(false);

  useEffect(() => {
    socket.emit('joinGame', { gameId, username });

    socket.on('updatePlayers', setPlayers);
    socket.on('revealVotes', (players) => {
      setPlayers(players);
      setRevealed(true);
    });

    return () => {
      socket.off('updatePlayers');
      socket.off('revealVotes');
    };
  }, [gameId, username]);

  const vote = (num) => {
    socket.emit('vote', { gameId, username, vote: num });
  };

  const reveal = () => {
    socket.emit('reveal', { gameId });
  };

  const fibonacciNumbers = [0, 1, 2, 3, 5, 8, 13, 21, 34, 65];

  return (
    <div>
      <h1>Game ID: {gameId}</h1>
      <ul>
        {players.map(p => (
          <li key={p.id}>{p.username} - {revealed ? p.vote : '❓'}</li>
        ))}
      </ul>
      {fibonacciNumbers.map(num => (
        <button key={num} onClick={() => vote(num)}>{num}</button>
      ))}
      <button onClick={reveal}>Reveal Votes</button>
    </div>
  );
}
