import React, { useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { socket } from '../socket';
import './LandingPage.css'; // Reuse the same styling

const JoinGame = () => {
  const [username, setUsername] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const { gameId } = useParams();
  const navigate = useNavigate();

  const handleJoinGame = () => {
    if (!username.trim()) {
      setError('Please enter your username');
      return;
    }

    setLoading(true);
    setError('');

    // Store username in sessionStorage
    sessionStorage.setItem('username', username.trim());

    // Emit join game event
    socket.emit('joinGame', {
      gameId: gameId,
      username: username.trim()
    });

    // Listen for join response
    socket.once('joinGameResponse', (response) => {
      setLoading(false);
      if (response.success) {
        // Navigate to the game page with the gameId
        navigate(`/game/${gameId}`);
      } else {
        setError(response.message || 'Failed to join the game');
      }
    });
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleJoinGame();
    }
  };

  return (
    <div className="landing-page">
      <div className="content-container">
        <div className="logo-container">
          <img 
            src={require('../images/koneLogo.png')} 
            alt="KONE Logo" 
            className="logo" 
          />
        </div>
        
        <h1 className="main-heading">Join Planning Poker</h1>
        <p className="sub-heading">Enter your username to join the game</p>
        
        <div className="form-container">
          <div className="input-group">
            <label htmlFor="username">Your Username</label>
            <input
              type="text"
              id="username"
              placeholder="Enter your username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              onKeyPress={handleKeyPress}
              disabled={loading}
              maxLength={20}
            />
          </div>
          
          {error && <div className="error-message">{error}</div>}
          
          <button 
            onClick={handleJoinGame}
            disabled={loading || !username.trim()}
            className="submit-btn"
          >
            {loading ? 'Joining...' : 'Join Game'}
          </button>
        </div>
        
        <div className="game-id-display">
          <p>Game ID: <strong>{gameId}</strong></p>
        </div>
      </div>
    </div>
  );
};

export default JoinGame;
