import { useTheme } from "../context/ThemeContext";
import { useParams } from "react-router-dom";

export default function Header() {
  const { theme, toggleTheme } = useTheme();
  const { gameId } = useParams();

  const copyLink = () => {
    navigator.clipboard.writeText(window.location.href);
    alert("Link copied!");
  };

  return (
    <header className="flex justify-between items-center p-4 bg-gray-200 dark:bg-gray-800">
      <div className="flex items-center gap-3">
        <h1 className="font-bold text-xl dark:text-white">Planning Poker</h1>
        {gameId && (
          <button
            onClick={copyLink}
            className="px-3 py-1 text-sm bg-blue-500 text-white rounded hover:bg-blue-600"
          >
            Copy Link
          </button>
        )}
      </div>
      <button
        onClick={toggleTheme}
        className="px-3 py-1 text-sm bg-gray-700 text-white rounded hover:bg-gray-900 dark:bg-yellow-400 dark:text-black dark:hover:bg-yellow-500"
      >
        {theme === "dark" ? "â˜€ Light" : "ðŸŒ™ Dark"}
      </button>
    </header>
  );
}
