import React, { useEffect, useState } from 'react';
import { useParams, useLocation } from 'react-router-dom';
import socket from '../socket';
import Header from '../components/Header';
import { fibonacciNumbers } from '../utils/fibonacci';

export default function GamePage() {
  const { gameId } = useParams();
  const location = useLocation();
  const [username, setUsername] = useState(location.state?.username || '');
  const [players, setPlayers] = useState([]);
  const [selectedNumber, setSelectedNumber] = useState(null);
  const [votesRevealed, setVotesRevealed] = useState(false);

  useEffect(() => {
    if (!username) {
      const name = prompt("Enter your username:");
      setUsername(name || "Guest");
    }
  }, [username]);

  useEffect(() => {
    if (!username) return;

    socket.emit('joinGame', { gameId, username });

    socket.on('playersUpdate', (players) => setPlayers(players));

    return () => {
      socket.off('playersUpdate');
    };
  }, [username, gameId]);

  const vote = (number) => {
    setSelectedNumber(number);
    socket.emit('vote', { gameId, username, vote: number });
  };

  const revealCards = () => {
    setVotesRevealed(true);
    socket.emit('revealVotes', { gameId });
  };

  const restartGame = () => {
    setSelectedNumber(null);
    setVotesRevealed(false);
    socket.emit('restartGame', { gameId });
  };

  const copyLink = () => {
    navigator.clipboard.writeText(window.location.href);
    alert("Link copied!");
  };

  return (
    <div>
      <Header onCopyLink={copyLink} />

      <div style={{ padding: '20px' }}>
        <h2>Game ID: {gameId}</h2>
        <h3>Players</h3>
        <ul>
          {players.map((p, index) => (
            <li key={index}>
              {p.username} — {votesRevealed ? p.vote ?? "No vote" : (p.vote !== null ? "✔" : "❌")}
            </li>
          ))}
        </ul>

        <div style={{ marginTop: '20px' }}>
          <h3>Pick a number</h3>
          {fibonacciNumbers.map((num) => (
            <button
              key={num}
              onClick={() => vote(num)}
              style={{
                background: selectedNumber === num ? 'lightgreen' : 'white',
                margin: '5px'
              }}
            >
              {num}
            </button>
          ))}
        </div>

        <div style={{ marginTop: '20px' }}>
          <button onClick={revealCards}>Reveal Cards</button>
          <button onClick={restartGame} style={{ marginLeft: '10px' }}>
            Restart Game
          </button>
        </div>
      </div>
    </div>
  );
}
