import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { io } from 'socket.io-client';

const socket = io('http://localhost:4000');

export default function GamePage() {
  const { gameId } = useParams();
  const navigate = useNavigate();
  const [players, setPlayers] = useState([]);
  const [vote, setVote] = useState(null);
  const [revealed, setRevealed] = useState(false);

  const fibonacciSeries = [0, 1, 2, 3, 5, 8, 13, 21, 34, 65];
  const username = localStorage.getItem('username');

  useEffect(() => {
    if (!username) {
      navigate(`/`);
      return;
    }

    socket.emit('joinGame', { gameId, username });

    socket.on('updatePlayers', (players) => {
      setPlayers(players);
    });

    socket.on('revealVotes', (players) => {
      setPlayers(players);
      setRevealed(true);
    });

    socket.on('gameRestarted', () => {
      setVote(null);
      setRevealed(false);
    });

    return () => {
      socket.off('updatePlayers');
      socket.off('revealVotes');
      socket.off('gameRestarted');
    };
  }, [gameId, username, navigate]);

  const handleCopyLink = () => {
    const link = `${window.location.origin}/game/${gameId}`;
    navigator.clipboard.writeText(link)
      .then(() => alert('Game link copied!'))
      .catch((err) => console.error('Failed to copy link', err));
  };

  const handleVote = (num) => {
    setVote(num);
    socket.emit('vote', { gameId, username, vote: num });
  };

  const handleReveal = () => {
    socket.emit('reveal', { gameId });
  };

  const handleRestart = () => {
    socket.emit('restartGame', { gameId });
  };

  // Vote counter
  const totalPlayers = players.length;
  const votesCast = players.filter(p => p.hasVoted || p.vote !== null).length;

  return (
    <div className="game-container">
      <div className="game-header">
        <h2>Game ID: {gameId}</h2>
        <button onClick={handleCopyLink}>Copy Link</button>
      </div>

      <h3>Players ({votesCast}/{totalPlayers} voted)</h3>
      <div className="players-list">
        {players.length === 0 ? (
          <p>No players yet...</p>
        ) : (
          players.map((p, idx) => (
            <div key={idx} className="player-card">
              {p.username} -{" "}
              {revealed
                ? p.vote !== null
                  ? p.vote
                  : "No vote"
                : p.hasVoted
                ? "✅ Voted"
                : "⏳ Waiting"}
            </div>
          ))
        )}
      </div>

      {!revealed && (
        <>
          <h3>Vote</h3>
          <div className="vote-section">
            {fibonacciSeries.map((num) => (
              <button
                key={num}
                onClick={() => handleVote(num)}
              >
                {num}
              </button>
            ))}
          </div>
        </>
      )}

      <div className="controls">
        <button
          onClick={handleReveal}
          disabled={votesCast !== totalPlayers || revealed}
        >
          Reveal Cards
        </button>
        <button onClick={handleRestart}>Restart Game</button>
      </div>
    </div>
  );
}
