import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import io from 'socket.io-client';

const socket = io('http://localhost:4000'); // adjust if backend runs elsewhere

export default function GamePage() {
  const { gameId } = useParams();
  const [username, setUsername] = useState('');
  const [players, setPlayers] = useState([]);
  const [revealed, setRevealed] = useState(false);

  const fibonacciNumbers = [0, 1, 2, 3, 5, 8, 13, 21, 34, 65];

  // On mount, join game with username from session/localStorage
  useEffect(() => {
    const storedUsername = localStorage.getItem('username') || `Player_${Math.floor(Math.random() * 1000)}`;
    setUsername(storedUsername);

    socket.emit('joinGame', { gameId, username: storedUsername });

    socket.on('updatePlayers', (updatedPlayers) => {
      setPlayers(updatedPlayers);
      setRevealed(false);
    });

    socket.on('revealVotes', (playersWithVotes) => {
      setPlayers(playersWithVotes);
      setRevealed(true);
    });

    socket.on('gameRestarted', () => {
      setRevealed(false);
    });

    return () => {
      socket.off('updatePlayers');
      socket.off('revealVotes');
      socket.off('gameRestarted');
    };
  }, [gameId]);

  const handleVote = (vote) => {
    socket.emit('vote', { gameId, username, vote });
  };

  const handleReveal = () => {
    socket.emit('reveal', { gameId });
  };

  const handleRestart = () => {
    socket.emit('restartGame', { gameId });
  };

  const handleCopyLink = () => {
    const gameLink = `${window.location.origin}/game/${gameId}`;
    navigator.clipboard.writeText(gameLink);
    alert('Game link copied!');
  };

  return (
    <div className="p-4">
      {/* Header */}
      <div className="flex justify-between items-center mb-6">
        <div className="flex items-center gap-3">
          <img src="/logo.png" alt="Logo" className="w-10 h-10" />
          <button onClick={handleCopyLink} className="px-3 py-1 bg-gray-200 rounded hover:bg-gray-300">
            Copy Link
          </button>
        </div>
        <div className="flex gap-2">
          <button onClick={handleReveal} className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600">
            Reveal
          </button>
          <button onClick={handleRestart} className="bg-orange-500 text-white px-4 py-2 rounded hover:bg-orange-600">
            Restart
          </button>
        </div>
      </div>

      {/* Players List */}
      <h2 className="text-lg font-semibold mb-4">Players in Game</h2>
      <div className="grid grid-cols-2 md:grid-cols-4 gap-4 mb-8">
        {players.map((player, index) => (
          <div key={index} className="p-4 border rounded shadow-sm bg-white">
            <p className="font-bold">{player.username}</p>
            <p className="text-gray-500">
              {revealed ? player.vote : player.vote !== null ? 'Voted ✅' : 'Not Voted ❌'}
            </p>
          </div>
        ))}
      </div>

      {/* Voting Buttons */}
      <h3 className="text-lg font-semibold mb-4">Select Your Vote</h3>
      <div className="flex flex-wrap gap-3">
        {fibonacciNumbers.map((num) => (
          <button
            key={num}
            onClick={() => handleVote(num)}
            className="px-4 py-2 bg-gray-200 rounded hover:bg-gray-300"
          >
            {num}
          </button>
        ))}
      </div>
    </div>
  );
}
