import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { v4 as uuidv4 } from 'uuid';
import './LandingPage.css';
import Navbar from "../components/Navbar";


export default function LandingPage() {
  const [username, setUsername] = useState('');
  const [fade,setFade] = useState(true);
  const [index,setIndex] = useState(0);
  const navigate = useNavigate();

  const message = [
    "We plan together, we win together...",
    "Every point counts when the team counts...",
    "Collaboration is the real currency...",
    "Estimate smart, deliver smarter...",
    "Turn uncertainty into clarity..."
  ]

  useEffect(() => {
    const interval = setInterval(() => {
      setFade(false); 
      setTimeout(() => {
        setIndex((prevIndex) => (prevIndex + 1) % message.length);
        setFade(true);
      }, 500);
    }, 5000);

    return () => clearInterval(interval);
  }, [message.length]);

  const handleStart = () => {
    if (!username.trim()) {
      alert('Please enter a username');
      return;
    }

    // Save username to localStorage
    localStorage.setItem('username', username);

    // Create a new game ID
    const gameId = uuidv4();

    // Redirect to game page
    navigate(`/game/${gameId}`);
  };

  return (
    <div>
      <div style={{ borderBottom: "1px solid #0068dfff" }}>
        <Navbar />
      </div>
      <div
        style={{ 
          top: "60%",
          left: "50%",
          transform: "translate(-50%, -50%)",
          textAlign: "center",
          display: "flex",
          flexDirection: "column",
          alignItems: "center"
        }}
      >
      <h1
        className="modeChange"
        style={{
          position: "absolute",
          top: "25%",
          left: "50%",
          transform: "translate(-50%, -50%)",
          color: "#004798ff",
          fontSize: "40px",
          textAlign: "center",
        }}
      >
        Welcome to Planning Poker!!
      </h1>

      <h2 className={`slide-text ${fade ? "slide-in" : "slide-out"}`}
        style={{
          position: "absolute",
          top: "40%",
          left: "50%",
          transform: "translate(-50%, -50%)",
          color: "#51b1ffff",
          fontSize: "26px",
          fontStyle: "italic",
        }}
      >
        {message[index]}
      </h2>

      
        <h2
          className="modeChange"
          style={{
            color: '#004798ff',
            fontSize: '20px',
            margin: '5px',
            fontWeight: '600'
          }}
        >
          Start a new session
        </h2>
        <input
          type="text"
          placeholder="Enter your username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          style={{
              width: "300px",
              padding: "12px",
              border: "2px solid #0068dfff",
              borderRadius: "8px",
              fontSize: "16px",
              outline: "none",
              transition: "border-color 0.3s",
              margin: "0 auto",
            }}
        />
        <button
          onClick={handleStart}
          style={{
            width: "150px",
            height: "50px",
            margin: "10px",
            fontSize: "20px",
            color: "white",
            backgroundColor: "#0068dfff",
            border: "none",
            borderRadius: "5px",
          }}
        >
          Start Game
        </button>
      </div>
    </div>
  );
}
