import { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import io from 'socket.io-client';

const socket = io('http://localhost:4000');

export default function GamePage() {
  const { gameId } = useParams();
  const navigate = useNavigate();
  const [username, setUsername] = useState(localStorage.getItem('username') || '');
  const [nameConfirmed, setNameConfirmed] = useState(!!localStorage.getItem('username'));
  const [players, setPlayers] = useState([]);

  useEffect(() => {
    if (nameConfirmed && username) {
      socket.emit('joinGame', { gameId, username });
    }

    socket.on('updatePlayers', (players) => {
      setPlayers(players);
    });

    return () => {
      socket.off('updatePlayers');
    };
  }, [nameConfirmed, username, gameId]);

  const handleJoin = () => {
    if (!username.trim()) {
      alert('Enter your name');
      return;
    }
    localStorage.setItem('username', username);
    setNameConfirmed(true);
  };

  if (!nameConfirmed) {
    return (
      <div className="flex flex-col items-center justify-center h-[80vh]">
        <h2 className="text-xl font-bold mb-4">Enter your username to join</h2>
        <input
          type="text"
          className="border px-4 py-2 mb-4"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
        />
        <button
          className="bg-blue-500 text-white px-4 py-2 rounded"
          onClick={handleJoin}
        >
          Join Game
        </button>
      </div>
    );
  }

  return (
    <div>
      <h1>Game ID: {gameId}</h1>
      <ul>
        {players.map(p => (
          <li key={p.id}>{p.username}</li>
        ))}
      </ul>
    </div>
  );
}
