import React, { useEffect, useState } from 'react';
import { useParams, useLocation } from 'react-router-dom';
import socket from '../socket';

export default function GamePage() {
  const { gameId } = useParams();
  const { state } = useLocation();
  const username = state?.username || 'Guest';

  const [players, setPlayers] = useState([]);
  const [revealed, setRevealed] = useState(false);

  const fibonacciNumbers = [0, 1, 2, 3, 5, 8, 13, 21, 34, 65];

  useEffect(() => {
    socket.emit('joinGame', { gameId, username });

    socket.on('updatePlayers', setPlayers);
    socket.on('revealVotes', (players) => {
      setPlayers(players);
      setRevealed(true);
    });
    socket.on('gameRestarted', () => {
      setRevealed(false);
    });

    return () => {
      socket.off('updatePlayers');
      socket.off('revealVotes');
      socket.off('gameRestarted');
    };
  }, [gameId, username]);

  const vote = (num) => {
    socket.emit('vote', { gameId, username, vote: num });
  };

  const reveal = () => {
    socket.emit('reveal', { gameId });
  };

  const restart = () => {
    socket.emit('restartGame', { gameId });
  };

  const copyLink = () => {
    const link = `${window.location.origin}/game/${gameId}`;
    navigator.clipboard.writeText(link)
      .then(() => alert("Game link copied to clipboard!"))
      .catch(err => console.error("Failed to copy link", err));
  };

  return (
    <div style={{ padding: '20px' }}>
      {/* Header with Copy Link */}
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
        <h1>Game ID: {gameId}</h1>
        <button onClick={copyLink}>üìã Copy Link</button>
      </div>

      <h2>Players</h2>
      <ul>
        {players.map(p => (
          <li key={p.id}>
            {p.username} ‚Äî {revealed ? p.vote ?? 'No vote' : '‚ùì'}
          </li>
        ))}
      </ul>

      <h3>Vote</h3>
      {fibonacciNumbers.map(num => (
        <button 
          key={num} 
          onClick={() => vote(num)} 
          style={{ margin: '5px' }}
        >
          {num}
        </button>
      ))}

      <div style={{ marginTop: '20px' }}>
        <button onClick={reveal} style={{ marginRight: '10px' }}>Reveal Votes</button>
        <button onClick={restart}>Restart Game</button>
      </div>
    </div>
  );
}
